import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Alert from "react-bootstrap/Alert";
import "./Dashboard.css";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { api } from "../../rtk/slices/productsSlice";
import { apiCategories } from "../../rtk/slices/categoriesSlice";
import { Link } from "react-router-dom";
import { MDBCol } from "mdbreact";
import DropdownButton from "react-bootstrap/DropdownButton";
import CategoriesTable from "./CategoriesTable";
import Dropdown from "react-bootstrap/Dropdown";
import ProductsTable from "./ProductsTable";
function Products() {
  const { products, error } = useSelector((state) => state.products);
  const categories = useSelector((state) => state.categories);

  const dispatch = useDispatch();
  const [form, setForm] = useState(false);
  // const [updateForm, setUpdate] = useState(false);
  useEffect(() => {
    dispatch(api());
    dispatch(apiCategories());
  }, [dispatch]);
  const [filterVal, setFilter] = useState("");
  const filter = (e) => {
    setFilter(e.target.value);
  };
  const productsList = () => {
    const e = products.filter((e) => e.productName.includes(filterVal));
    return e;
  };
  // const categoriesTable = () => {
  //   const e = categories.filter((e) => e.categoryName.includes(filterVal));
  //   return e;
  // };
  let section = null;
  if (
    window.location.pathname.includes("/Dashboard/MangeProducts")
    // window.location.pathname === "/Dashboard/MangeProducts/Delete/"
  ) {
    section = productsList().length > 0 && (
      <ProductsTable
        formState={form}
        setForm={setForm}
        // updateForm={updateForm}
        // setUpdate={setUpdate}
      />
    );
  }
  if (
    window.location.pathname.includes("/Dashboard/MangeCategories")
    // window.location.pathname === "/Dashboard/MangeCategories/Delete/"
  ) {
    section = categories.length > 0 && (
      <CategoriesTable formState={form} setForm={setForm} />
    );
  }

  return (
    <>
      {error && <Alert color="error">Error in Api</Alert>}
      <Row>
        <Col md="2" className="p-0">
          <div className="control-side ">
            <div className="admin-avatar">
              <div className="avatar-img">
                <img src="../img/admin.png" alt="NotFound"></img>
              </div>
              <h5>Admin Dashboard</h5>
            </div>
            <div className="control-list">
              <ul>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeProducts">
                    <i class="fa-solid fa-basket-shopping-simple"></i> Mange
                    Products
                  </Link>
                </li>
                <li>
                  <Link className="nav-link" to="/Dashboard/MangeCategories">
                    Mange Categories
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </Col>
        <Col md="10" className="p-0">
          <div className="dashboard-nav">
            <MDBCol md="3">
              <form className="form-inline mt-4 mb-4 search-form">
                <i class="fa-solid fa-magnifying-glass"></i>{" "}
                <input
                  className="form-control form-control-sm ml-3 input-style"
                  type="text"
                  placeholder=""
                  value={filterVal}
                  aria-label="Search"
                  onChange={filter}
                />
              </form>
            </MDBCol>

            <Button onClick={() => setForm(true)}>
              {window.location.pathname === "/Dashboard/MangeCategories"
                ? "AddCategory"
                : "AddProduct"}
            </Button>
          </div>

          <div className="p-4">{section}</div>
        </Col>
      </Row>
    </>
  );
}
export default Products;
