import Wrapper from "../../Wrapper/Dashboard";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import AddProduct from "../add/Add";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import CloseButton from "react-bootstrap/CloseButton";
import { useAppContext } from "../../context/appContext";
import UpdateProduct from "../update/UpdateProduct";
import Table from "react-bootstrap/Table";
import Form from "react-bootstrap/Form";

const ProductsTable = () => {
  const { deleteItem, setUpdatePro } = useAppContext();
  const { products, updateProId } = useSelector((state) => state.products);

  const HandleUpdate = (e, size) => {
    const data = {
      sizeName: size.sizeName,
      elementState: !size.elementState,
      _id: size._id,
    };

    UpdateItem(e, data);
  };
  return (
