    //             </Col>
    //           ))}
    //         </>
    //       )}
    //     </Row>
    //     <AddProduct />
    //   </Wrapper>
    // </>
    <>
      <Wrapper>
        {updateProId ? (
          <UpdateProduct />
        ) : (
          <>
            <Table>
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>State</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product._id}>
                    <td>{product.name}</td>

                    <td>
                      {" "}
                      <Form>
                        <Form.Check
                          type="switch"
                          id={product._id}
                          label="Active"
                          name={product.name}
                          checked={product.elementState === true ? true : false}
                          onChange={(e) => HandleUpdate(e, product)}
                        />
                      </Form>
                    </td>
                    <td>
                      {" "}
                      <span className="delete-section">
                        <CloseButton onClick={() => deleteItem(product._id)} />
                      </span>
                      <span className="edit-section">
                        <Link
                          to={`/dashboard/MangeColors`}
                          onClick={() => setUpdatePro(product)}
                        >
                          <i className="fa-regular fa-pen-to-square"></i>
                        </Link>
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        )}
        <AddProduct />
      </Wrapper>
    </>
  );
};

export default ProductsTable;
